import React, {useState} from "react";
import "./StartPage.css";
import LoginBox from "../components/LoginBox";
import SignupBox from "../components/SignupBox";

function StartPage() {
  const [num, setcount] = useState(0);
  let box;
  if(num===1){
    box=<SignupBox></SignupBox>
  }
  else{
    box=<LoginBox></LoginBox>
  }
  const style ={
    transform:num >= 1 ? 'translateX(100%)' : 'none',
    
    // position:num===1?'static':'none',
    // margin:num===1?'0px auto':'none',
    transition: 'transform 1s ease',
    width:num===2?'900px':'none',
    // transition: 'transform 1s ease',
  }
  return (
    <div className="Background">
      <div className="WhiteBox" style={style}>
        {box}
        <button onClick={()=>{setcount(num+1)}}>hu</button>
        <p>{num}</p>
      </div>
    </div>
  );
}

export default StartPage;
