import React, {useState} from "react";
import "./StartPage.css";
import LoginBox from "../components/LoginBox";
import SignupBox from "../components/SignupBox";

function StartPage() {
  const [num1] = useState(0);
  const [num2] = useState(0);
  // const [num1,setcount] = useState(0);
  // const [num2,setcount2] = useState(0);
  let box;
  if(num1===1){
    box=<SignupBox></SignupBox>
  }
  else{
    box=<LoginBox></LoginBox>
  }
  function testClick() {
    if(num1 !== 1){
      num1+=1;
      num2+=1;
    }
    else{
      num1+=1;
      num2+=1;
    }
  }
  
  const style ={
    transform:num1 === 1 ? 'translateX(100%)' : 'none',
    width:num2===2?'1500px':'none',
    // position:num===2?'static':'none',
    // display:num===2?'inline-block':'flex',
    // margin:num===2?'26px 0px 0px 0px':'none',
    // width:num===2?'1500px':'none',
    transition: 'transform 1s ease, width 1s',
  }
  // const style2 ={
  //   width:num2===2?'1500px':'none',
  //   transition: 'width 1s',
  // }
  // const style2={
  //   margi
  // }
  return (
    <div className="Background">
      <div className="WhiteBox" style={style}>
        {box}
        <button onClick={testClick}>hu</button>
        {/* <button onClick={()=>{setcount2(num2+1)}}>hd</button> */}
        <p>{num1}</p>
        <p>{num2}</p>
      </div>
    </div>
  );
}

export default StartPage;
